mapper.select:
select IF(sum(c.addDiagnosis) is NULL, 0, sum(c.addDiagnosis)) as dayAdd
from city a,
     city b,
     yiqing c
where a.id = b.pId
  and a.id = #{id}
  and c.cId = b.id
  and entryDate = date_sub(curdate(), interval 1 day);

select IF(sum(c.addDiagnosis) is NULL, 0, sum(c.addDiagnosis)) as dayAdd
from city b,
     yiqing c
where b.id = #{id}
  and c.cId = b.id
  and entryDate = date_sub(curdate(), interval 1 day);
